import React, { useState, useEffect, useContext } from "react";
import ProductCard from "../Components/ProductCard";
import { ProductContext } from "../context/context";

export default function Men() {
  const context = useContext(ProductContext);
  let { filterProducts, handleChange ,tshirt,trousers,jeans} = context;
  filterProducts = filterProducts.filter((el) => el.gender === "Male");

  return (
    <div className="Men">
      <div className="Filter">
        <h2>Filters</h2>
        <div className="Filter__options">
          <h3 className="Filter__title">Categories</h3>
          <div className="Filter__options--jeans">
            <h4>Jeans</h4>
            <input type="checkbox" checked={jeans} onChange={handleChange} />
          </div>
          <div className="Filter__options--tshirts">
            <h4>T-shirts</h4>
            <input type="checkbox" checked={tshirt} onChange={handleChange} />
          </div>
          <div className="Filter__options--trousers">
            <h4>Trousers</h4>
            <input type="checkbox" checked={trousers} onChange={handleChange} />
          </div>
        </div>
      </div>
      <div className="ProductsList">
        {filterProducts.map((item) => {
          return <ProductCard product={item} />;
        })}
      </div>
    </div>
  );
}
