import React, { useState, useEffect ,useContext} from "react";
import items from "../data";
import ProductCard from "../Components/ProductCard";
import {ProductContext} from "../context/context";

const handleChange=(event)=>{
    console.log(event.target.name);
    items.filter(el=>el.category===event.target.name);
};

export default function Men() {
//   const [state, setState] = useState([]);
//   useEffect(() => {
//     const tempItem = items.filter((el) => el.gender === "Male");
//     setState(tempItem);
//     console.log(tempItem);
//   },[]);

    const context=useContext(ProductContext);
    const {products}=context;

  return (
    <div className="Men">
      <div className="Filter">
        <h2>Filters</h2>
        <div className="Filter__options">
          <h3 className="Filter__title">Categories</h3>
          <div className="Filter__options--jeans">
            <h4>Jeans</h4>
            <input type="checkbox" name="jeans" onChange={handleChange} />
          </div>
          <div className="Filter__options--tshirts">
            <h4>T-shirts</h4>
            <input type="checkbox" name="tshirt" onChange={handleChange} />
          </div>
          <div className="Filter__options--trousers">
            <h4>Trousers</h4>
            <input type="checkbox" name="trousers" onChange={handleChange} />
          </div>
        </div>
      </div>
      <div className="ProductsList">
        {products.map((item) => {
          return <ProductCard product={item} />;
        })}
      </div>
    </div>
  );
}
