import React, { Component } from "react";
import items from "../data";

const ProductContext = React.createContext();

export default class ProductProvider extends Component {
  state = {
    products: [],
    categories: ["jeans","tshirts","trousers"],
    price: 0,
    maxPrice: 0,
    minPrice: 0,
    loading: true,
  };

  componentDidMount() {
    let Products = items;
    let maxPrice = Math.max(...Products.map((items) => items.price));
    this.setState({
      products: Products,
      price: maxPrice,
      maxPrice: maxPrice,
      loading: false,
    });
  }

  handleChange(event){
      const selectedCat=event.target.name;
      let {products}=this.state;
      let tempProducts=[...products];
    // for(let i=0;i<this.state.categories.length;i++){
    //     if(categories[i]){
    //         tempProducts.filter((el)=>el.categories===el);
    //     }
    // }
    tempProducts=tempProducts.filter(el=>el.categories===selectedCat);
    this.setState({
        products:tempProducts
    })
  }

  render() {
    return (
      <ProductContext.Provider value={{ ...this.state,handleChange:this.handleChange }}>
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer, ProductContext };

export function withRoomConsumer(Component) {
  return function ConsumerWrapper(props) {
    return (
      <ProductConsumer>
        {(value) => <Component {...props} context={value} />}
      </ProductConsumer>
    );
  };
}
