import React, { Component } from "react";
import items from "../data";

const ProductContext = React.createContext();

export default class ProductProvider extends Component {
  state = {
    products: [],
    price: 0,
    maxPrice: 0,
    minPrice: 0,
    loading: true,
    tshirt:false,
    jeans:false,
    trousers:false
  };

  componentDidMount() {
    let Products = items;
    let maxPrice = Math.max(...Products.map((items) => items.price));
    this.setState({
      products: Products,
      price: maxPrice,
      maxPrice: maxPrice,
      loading: false,
    });
  }

  handleChange=(event)=> {
    const target = event.target;
    const value = target.type === "checkbox" ? target.checked : target.value;
    const name = target.name;
    console.log(name, value);

    this.setState(
      {
        [name]: value
      },
      this.filterProducts
    );
  }

  filterProducts=()=>{
    let {
        products,
        price,
        maxPrice,
        minPrice,
        loading,
        tshirt,
        jeans,
        trousers
    }=this.state;
    let tempProducts=[...products];
    if(tshirt){
        tempProducts=tempProducts.filter(prod=>prod.category==="tshirt");
    }
    if(jeans){
        tempProducts=tempProducts.filter(prod=>prod.category==="jeans");
    }
    if(trousers){
        tempProducts=tempProducts.filter(prod=>prod.category==="trousers");
    }

    this.setState({
        products:tempProducts
    })

  }

  render() {
    return (
      <ProductContext.Provider
        value={{ ...this.state, handleChange: this.handleChange}}
      >
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer, ProductContext };

export function withRoomConsumer(Component) {
  return function ConsumerWrapper(props) {
    return (
      <ProductConsumer>
        {(value) => <Component {...props} context={value} />}
      </ProductConsumer>
    );
  };
}
