import React, { Component } from "react";
import items from "../data";

const ProductContext = React.createContext();

export default class ProductProvider extends Component {
  state = {
    products: [],
    filterProducts:[],
    categories: ["jeans", "tshirts", "trousers"],
    price: 0,
    maxPrice: 0,
    minPrice: 0,
    loading: true,
  };

  componentDidMount() {
    let Products = items;
    let maxPrice = Math.max(...Products.map((items) => items.price));
    this.setState({
      products: Products,
      price: maxPrice,
      maxPrice: maxPrice,
      loading: false,
    });
  }

  handleChange=(event)=> {
    let { products } = this.state;
    let tempProducts = [...products];
    if(event.target.checked){
        tempProducts = tempProducts.filter((el) => el.category === event.target.name);this.filterProducts(tempProducts);
    }else{
        this.filterProducts(products);
    }


  }

  filterProducts=(newProduct)=>{
    this.setState({
        filterProducts:newProduct
    })
  }

  render() {
    return (
      <ProductContext.Provider
        value={{ ...this.state, handleChange: this.handleChange}}
      >
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}

const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer, ProductContext };

export function withRoomConsumer(Component) {
  return function ConsumerWrapper(props) {
    return (
      <ProductConsumer>
        {(value) => <Component {...props} context={value} />}
      </ProductConsumer>
    );
  };
}
