import React, { useState, useEffect } from "react";
import items from "../data";
import ProductCard from "../Components/ProductCard";

const handleChange=(event)=>{
    console.log(event.target.value)
};

export default function Men() {
  const [state, setState] = useState([]);
  useEffect(() => {
    const tempItem = items.filter((el) => el.gender === "Male");
    setState(tempItem);
    console.log(tempItem);
  }, []);

  return (
    <div className="Men">
      <div className="Filter">
        <h2>Filters</h2>
        <div className="Filter__options">
          <h3 className="Filter__title">Categories</h3>
          <div className="Filter__options--jeans">
            <h4>Jeans</h4>
            <input type="checkbox" id="jeans" onChange={handleChange} />
          </div>
          <div className="Filter__options--tshirts">
            <h4>T-shirts</h4>
            <input type="checkbox" onChange={handleChange} />
          </div>
          <div className="Filter__options--trousers">
            <h4>Trousers</h4>
            <input type="checkbox" onChange={handleChange} />
          </div>
        </div>
      </div>
      <div className="ProductsList">
        {state.map((item) => {
          return <ProductCard product={item} />;
        })}
      </div>
    </div>
  );
}
