import React,{useContext} from "react";
import {ProductContext} from "../context/context";

export default function LoginScreen() {
    const context=useContext(ProductContext);
    const {loginHandler}=context;
    let email="";
    let pass="";

  return (
    <div className="MainLogin">
      <div className="MainLogin__signup">signup</div>
      <div className="MainLogin__login">
        <h1>Welcome To Extreme-Joy</h1>
        <h1>Sign-In Now!</h1>
        <div className="MainLogin__login--input">
          <input className="MainLogin__login--input-1" name="email" type="text" placeholder="Email" value={email}/>
          <input className="MainLogin__login--input-1" name="password" type="text" placeholder="Password" value={pass} />
          <label>
              <input type="checkbox"/>
               Remember me
          </label>
          <button onClick={()=>loginHandler(email,pass)}>Sign-In</button>
        </div>
      </div>
    </div>
  );
}
