import React, { useEffect, useContext } from "react";
import { ProductContext } from "../context/context";
// {props.match.params.slug}

export default function ProductDetailScreen(props) {
  const context = useContext(ProductContext);
  let { products, loading } = context;
  const product = products.find((el) => el._id === props.match.params.slug);
  if (!loading) console.log(product.photos);

  return (
    <React.Fragment>
      {loading === true ? (
        <div>Loading</div>
      ) : (
        <div className="ProductDetail">
          <div className="ProductDetail__image">
            <img src={product.photos} />
          </div>

          <div className="ProductDetail__content">
            <h1 className="ProductDetail__content--heading">{product.title}</h1>
            <h2 className="ProductDetail__content--subheading">product.subtitle</h2>
          </div>
        </div>
      )}
    </React.Fragment>
  );
}
